int main ( ) { int n , t , a , b , j , y ; t = 0 ; scanf ( "%d" , & n ) ; while ( n > 0 ) { t = 0 ; scanf ( "%d%d" , & a , & b ) ; if ( a > b ) { for ( j = 1 ; j <= b ; j ++ ) { if ( a % j == 0 && b % j == 0 ) { if ( j > t ) t = j ; } } y = a * b / t ; printf ( "%d %d\n" , t , y ) ; } else { for ( j = 1 ; j <= a ; j ++ ) { if ( a % j == 0 && b % j == 0 ) { if ( j > t ) t = j ; } } y = a * b / t ; printf ( "%d %d\n" , t , y ) ; } n -- ; } }