int main ( ) { int a , b , gcd , lcm , i , temp , t ; scanf ( "%d" , & t ) ; while ( t -- ) { if ( a < b ) { a = a ; b = b ; } else { temp = a ; a = b ; b = temp ; } scanf ( "%d%d" , & a , & b ) ; for ( i = 1 ; i <= a ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { gcd = i ; } } lcm = ( a * b ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } }