int main ( ) { int n , a [ 6000 ] , i , b , j , k , c , s , t , test , z ; long long int sum , max ; scanf ( "%d" , & test ) ; for ( z = 0 ; z < test ; z ++ ) { max = 0 ; scanf ( "%d" , & n ) ; s = n * ( n + 1 ) / 2 ; for ( i = 1 ; i <= s ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 1 ; i <= n ; i ++ ) { b = i * ( i - 1 ) / 2 + 1 ; sum = a [ b ] ; t = b ; for ( j = i + 1 ; j <= n ; j ++ ) { t = t + j ; sum = sum + a [ t ] ; } if ( b > 1 ) { for ( k = 1 ; k < i ; k ++ ) { c = k * ( k - 1 ) / 2 + 1 ; sum = sum + a [ c ] ; } } if ( sum >= max ) { max = sum ; } } printf ( "%lld\n" , max ) ; } return 0 ; }