int main ( ) { int k , n ; long long int a [ 1000000 ] , b [ 1000000 ] , hcf , lcm , max , min , r ; scanf ( "%d" , & n ) ; for ( k = 0 ; k < n ; k ++ ) { scanf ( "%lld" , & a [ k ] ) ; scanf ( "%lld" , & b [ k ] ) ; if ( b [ k ] > a [ k ] ) { max = b [ k ] ; min = a [ k ] ; } else if ( a [ k ] > b [ k ] ) { max = a [ k ] ; min = b [ k ] ; } if ( a [ k ] == b [ k ] ) { hcf = a [ k ] ; } else { do { r = max % min ; max = min ; min = r ; } while ( r != 0 ) ; { hcf = max ; } } lcm = ( a [ k ] * b [ k ] ) / hcf ; printf ( "%lld %lld\n" , lcm , hcf ) ; } return 0 ; }