long long int gcd ( int a , int b ) { if ( b == 0 || a == 0 ) return 0 ; if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } main ( ) { int t , a , b ; long long int gcd1 , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; gcd1 = gcd ( a , b ) ; lcm = ( a / gcd1 ) ; lcm = lcm * b ; printf ( "%lld %lld\n" , gcd1 , lcm ) ; } }