int main ( ) { int tst , len , i , j ; scanf ( "%d" , & tst ) ; for ( i = 0 ; i < tst ; i ++ ) { long long int ans ; scanf ( "%d" , & len ) ; long int min ; long int chk [ len ] ; for ( j = 0 ; j < len ; j ++ ) { scanf ( "%d" , & chk [ j ] ) ; } min = chk [ 0 ] ; for ( j = 0 ; j <= len / 2 ; j ++ ) { if ( chk [ j ] <= chk [ len - 1 - j ] ) { if ( chk [ j ] <= min ) { min = chk [ j ] ; } } else { if ( chk [ len - 1 - j ] <= min ) { min = chk [ len - 1 - j ] ; } } } ans = ( len - 1 ) * min ; printf ( "%lld\n" , ans ) ; } }