int prev_array [ 1000 ] ; int main ( ) { int t , cnt ; scanf ( "%d" , & t ) ; while ( t -- ) { int n ; scanf ( "%d" , & n ) ; int arr [ 2 * n ] ; int i , k , j ; int maxi ; for ( i = 0 ; i < n ; i ++ ) arr [ i ] = 0 ; for ( i = 0 ; i < n ; i ++ ) { int inp [ i + 1 ] ; for ( j = 0 ; j <= i ; j ++ ) scanf ( "%d" , & inp [ j ] ) ; if ( i == 0 ) { arr [ 0 ] = inp [ 0 ] ; } else { for ( k = 0 ; k <= i ; k ++ ) prev_array [ k ] = arr [ k ] ; cnt = 0 ; for ( k = 0 ; k < i ; k ++ ) { int x = prev_array [ k ] ; arr [ cnt ] = x + inp [ k ] ; cnt ++ ; arr [ cnt ] = x + inp [ k + 1 ] ; cnt ++ ; } } } maxi = INT_MIN ; for ( i = 0 ; i < cnt ; i ++ ) { if ( arr [ i ] > maxi ) maxi = arr [ i ] ; } printf ( "%d\n" , maxi ) ; } }