int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int i , n , sum = 0 , min , * a ; scanf ( "%d" , & n ) ; a = ( int * ) malloc ( n * sizeof ( int ) ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , a + i ) ; min = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] >= min ) sum += min ; else { sum += a [ i ] ; min = a [ i ] ; } } printf ( "%d\n" , sum ) ; } return 0 ; }