int main ( ) { int n , i ; scanf ( "%d" , & n ) ; for ( i = 1 ; i <= n ; i ++ ) { int a , b , x , y , t , lcm , gcd ; scanf ( "%d%d" , & x , & y ) ; a = x ; b = y ; while ( b != 0 ) { t = b ; b = a % b ; a = t ; } gcd = a ; lcm = ( x * y ) / gcd ; printf ( "%d\t" , gcd ) ; printf ( "%d\n" , lcm ) ; } return 0 ; }