int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a [ 50 ] , n , i , j , min = 0 , temp = 0 , sum = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 1 ; i < n - 1 ; i ++ ) { for ( j = 0 ; j < n - i - 1 ; j ++ ) { if ( a [ j ] > a [ j + 1 ] ) { temp = a [ j ] ; a [ j ] = a [ j + 1 ] ; a [ j + 1 ] = temp ; } } } printf ( "%d" , sum = sum + ( a [ 0 ] * ( n - 1 ) ) ) ; } }