int main ( ) { long long int i , j , n , b [ 50000 ] ; int t , min = 0 ; scanf ( "%d" , & t ) ; if ( t >= 1 && t <= 10 ) { while ( t -- > 0 ) { scanf ( "%lld" , & n ) ; if ( n >= 2 ) { for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & b [ i ] ) ; } min = b [ 0 ] ; for ( j = 1 ; j < n ; j ++ ) { if ( min > b [ j ] ) { min = b [ j ] ; } } printf ( "%lld" , min * ( n - 1 ) ) ; min = 0 ; } } return 0 ; }