int main ( void ) { int t , n , k , temp , sum , i , j ; scanf ( "%d" , & t ) ; while ( t -- ) { sum = 0 ; scanf ( "%d" , & n ) ; int ar [ n ] [ n ] ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) scanf ( "%d" , & ar [ i ] [ j ] ) ; } for ( i = 1 ; i < n ; i ++ ) { k = 0 ; for ( j = 0 ; j < i ; j ++ ) { if ( ar [ i ] [ k ] < ar [ i ] [ j ] ) k = j ; } temp = ar [ i ] [ k ] ; ar [ i ] [ k ] = ar [ i ] [ 0 ] ; ar [ i ] [ 0 ] = temp ; } for ( i = 0 ; i < n ; i ++ ) sum += ar [ i ] [ 0 ] ; printf ( "%d\n" , sum ) ; } return 0 ; }