int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t > 0 ) { int n ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( int i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } int cost = 0 ; for ( int i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { a [ i + 1 ] = a [ i + 1 ] ; cost = cost + a [ i + 1 ] ; } else if ( a [ i + 1 ] >= a [ i ] ) { a [ i + 1 ] = a [ i ] ; cost = cost + a [ i ] ; } } printf ( "%d\n" , cost ) ; t -- ; } return 0 ; }