int main ( ) { int t , n , i , count , count1 , a , b ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; count = n ; scanf ( "%d" , & a ) ; n -- ; while ( n ) { count1 = 1 ; scanf ( "%d" , & b ) ; n -- ; while ( a <= b ) { count1 ++ ; a = b ; if ( n ) { scanf ( "%d" , & b ) ; n -- ; } else break ; } a = b ; if ( count1 ) { count = count + count1 * ( count1 - 1 ) / 2 ; } } printf ( "%d\n" , count ) ; } return 0 ; }