int gcd ( a , b ) { int num = 0 , den = 0 , rem , c ; if ( a > b ) { num = a ; den = b ; } else if ( a < b ) { num = b ; den = a ; } else { num = a ; den = b ; } rem = num % den ; while ( rem != 0 ) { num = den ; den = rem ; rem = num % den ; } c = den ; return ( c ) ; } int main ( ) { int A , B , t , g , l ; scanf ( "%d" , & t ) ; while ( t -- > 0 ) { scanf ( "%d%d" , & A , & B ) ; g = gcd ( A , B ) ; l = ( A * B ) / g ; printf ( "%d %d" , g , l ) ; } return ( 0 ) ; }