int main ( ) { int t , a , b , l , max , i , min ; scanf ( "%d" , & t ) ; while ( t -- ) { max = 1 ; scanf ( "%d%d" , & a , & b ) ; for ( i = 2 ; ( ( i <= a ) && ( i <= b ) ) ; i ++ ) { if ( ( a % i == 0 ) && ( b % i == 0 ) ) max = i ; } if ( a > b ) min = b ; else min = a ; for ( i = min ; i <= ( a * b ) ; i ++ ) { if ( ( i % a == 0 ) && ( i % b == 0 ) ) { l = i ; break ; } } printf ( "%d %d" , max , l ) ; } return 0 ; }