int gcd ( int a , int b ) { int i ; if ( a > b ) for ( i = b ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) return i ; } else if ( b > a ) for ( i = a ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) return i ; } if ( a = b ) return a ; } int lcm ( int a , int b ) { int x , y ; x = a ; y = b ; while ( a != b ) { if ( a > b ) a = a - b ; else b = b - a ; } return x * y / a ; } int main ( ) { int t , x [ 1000 ] , y [ 1000 ] , i ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) scanf ( "%d %d" , & x [ i ] , & y [ i ] ) ; for ( i = 0 ; i < t ; i ++ ) printf ( "%d %d\n" , gcd ( x [ i ] , y [ i ] ) , lcm ( x [ i ] , y [ i ] ) ) ; }