int main ( ) { int i , t , n , min , j , a [ 50000 ] ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%d" , & a [ j ] ) ; min = a [ 0 ] ; for ( j = 1 ; j < n ; j ++ ) { if ( min > a [ j ] ) min = a [ j ] ; } printf ( "%d\n" , ( min ) * ( n - 1 ) ) ; } return 0 ; }