void main ( ) { int t , a [ 100 ] , i , n , d = 0 , count = 0 , j , k ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; } d = 0 ; count = 0 ; for ( j = 0 ; j < n ; j ++ ) { if ( n > 1 ) { d = d + count ; count = 1 ; for ( k = j ; k < n ; k ++ ) { if ( a [ k ] < a [ k + 1 ] ) count ++ ; else break ; } } else d = 1 ; } printf ( "%d\n" , d ) ; } }