void swap ( long int * x , long int * y ) { * x = * x + * y ; * y = * x - * y ; * x = * x - * y ; } long int gcd ( long int a , long int b ) { long int temp ; while ( b != 0 ) { temp = a ; a = b ; b = temp % b ; } return a ; } int main ( ) { int T ; long int A , B , temp ; scanf ( "%d" , & T ) ; for ( int i = 0 ; i < T ; ++ i ) { scanf ( "%ld" , & A ) ; scanf ( "%ld" , & B ) ; if ( A < B ) swap ( & A , & B ) ; temp = gcd ( A , B ) ; printf ( "%ld " , temp ) ; printf ( "%lld\n" , A * B / temp ) ; } return 0 ; }