int main ( ) { int T , num1 [ 1000 ] [ 2 ] , gcd , lcm , rem , numer , denom , i ; scanf ( "%d" , & T ) ; for ( i = 0 ; i < T ; i ++ ) scanf ( "%d %d" , & num1 [ i ] [ 0 ] , & num1 [ i ] [ 1 ] ) ; for ( i = 0 ; i < T ; i ++ ) { if ( num1 [ i ] [ 0 ] > num1 [ i ] [ 1 ] ) { numer = num1 [ i ] [ 0 ] ; denom = num1 [ i ] [ 1 ] ; } else { numer = num1 [ i ] [ 1 ] ; denom = num1 [ i ] [ 0 ] ; } rem = num1 [ i ] [ 0 ] % num1 [ i ] [ 1 ] ; while ( rem != 0 ) { numer = denom ; denom = rem ; rem = numer % denom ; } gcd = denom ; lcm = num1 [ i ] [ 0 ] * num1 [ i ] [ 1 ] / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }