int gcd ( ) ; int main ( ) { int t , a , b , c , H [ 1000 ] , L [ 1000 ] ; scanf ( "%d" , & t ) ; for ( a = 0 ; a < t ; a ++ ) { scanf ( "%d%d" , & b , & c ) ; H [ a ] = gcd ( b , c ) ; L [ a ] = ( b * c ) / H [ a ] ; } for ( a = 0 ; a < t ; a ++ ) { printf ( "\n%d %d" , H [ a ] , L [ a ] ) ; } return 0 ; } int gcd ( int b , int c ) { if ( b == 0 ) { return c ; } else { while ( c != 0 ) { if ( b > c ) { b = b - c ; } else { c = c - b ; } } return b ; } }