int main ( ) { int tst , i , j , k , row , max ; scanf ( "%d" , & tst ) ; int ans [ tst ] ; for ( i = 0 ; i < tst ; i ++ ) { max = 0 ; int sum = 0 ; scanf ( "%d" , & row ) ; int ind [ row ] ; int tri [ row ] [ row ] ; for ( j = 1 ; j <= row ; j ++ ) { ind [ j - 1 ] = 0 ; for ( k = 0 ; k < j ; k ++ ) { tri [ j - 1 ] [ k ] = 0 ; scanf ( "%d" , & tri [ j - 1 ] [ k ] ) ; } } while ( ind [ 0 ] < row ) { for ( k = 0 ; k < row ; k ++ ) { int temp = ind [ row - k - 1 ] ; sum += tri [ k ] [ temp ] ; } if ( sum >= max ) { max = sum ; } sum = 0 ; if ( ind [ 0 ] == row - 1 ) { break ; } for ( k = 0 ; k < row - 1 ; k ++ ) { if ( ( ind [ k ] == ind [ k + 1 ] ) && ind [ k + 1 ] ) { ind [ k ] ++ ; break ; } if ( ! ind [ k ] && ! ind [ k + 1 ] ) { for ( int p = 0 ; p < k + 1 ; p ++ ) { ind [ p ] = 1 ; } break ; } } } ans [ i ] = max ; } for ( i = 0 ; i < tst ; i ++ ) { printf ( "%d\n" , ans [ i ] ) ; } return 0 ; }