int main ( ) { int t ; scanf ( "%d" , & t ) ; int i ; for ( i = 0 ; i < t ; i ++ ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; int g , l ; if ( a > b ) { int D , d , temp ; D = a ; d = b ; while ( D % d != 0 ) { temp = d ; d = D % d ; D = temp ; } g = d ; } else { int D , d , temp ; D = b ; d = a ; while ( D % d != 0 ) { d = temp ; d = D % d ; D = temp ; } g = d ; } l = ( a * b ) / g ; printf ( "%d %d \n" , g , l ) ; } }