int main ( ) { int t , b , i , s , c = 0 ; int a [ 50 ] ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & b ) ; for ( i = 0 ; i < b ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 0 ; i < b - 1 ; i ++ ) { if ( a [ i ] < a [ i + 1 ] ) s = a [ i ] ; else s = a [ i + 1 ] ; c = c + s ; } printf ( "%d" , c ) ; } return 0 ; }