int main ( ) { int t , i , n , q , j , in , a [ 100000 ] , k , sum ; scanf ( "%d" , & t ) ; while ( t -- ) { sum = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 1 ; i < n ; i ++ ) { q = 100000 ; for ( j = 0 ; j < n - i + 1 ; j ++ ) { if ( q > a [ j ] ) { q = a [ j ] ; in = j ; } } if ( in == 0 ) { for ( k = in + 1 ; k < n - i ; k ++ ) a [ k ] = a [ k + 1 ] ; sum += q ; } else { if ( a [ in + 1 ] < a [ in - 1 ] ) { for ( k = in - 1 ; k < n - i ; k ++ ) a [ k ] = a [ k + 1 ] ; sum += q ; } else { for ( k = in + 1 ; k < n - i ; k ++ ) a [ k ] = a [ k + 1 ] ; sum += q ; } } } printf ( "%d\n" , sum ) ; } return 0 ; }