long long int gcd ( long long int a , long long int b ) { while ( a != b ) { if ( a > b ) a = a - b ; if ( a < b ) b = b - a ; } printf ( "%lld" , a ) ; printf ( " " ) ; return a ; } int main ( ) { int t ; long long int num1 , num2 , temp ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lld%lld" , & num1 , & num2 ) ; temp = ( num1 * num2 ) / gcd ( num1 , num2 ) ; printf ( "%lld\n" , temp ) ; } return 0 ; }