int gcd ( int a , int b ) ; int main ( void ) { int t ; scanf ( "%d" , & t ) ; if ( t >= 1 && t <= 1000 ) { while ( t -- ) { long long int a , b , g , l ; scanf ( "%lld%lld" , & a , & b ) ; if ( a >= 1 && b >= 1 && a <= 1000000 && b <= 1000000 ) { if ( a > b ) { g = gcd ( a , b ) ; } else { g = gcd ( b , a ) ; } l = ( a * b ) / g ; printf ( "%lld %lld\n" , g , l ) ; } } } return 0 ; } int gcd ( int a , int b ) { long long int rem = a % b ; a = b ; b = rem ; if ( b == 0 ) { return a ; } else { gcd ( a , b ) ; } }