int main ( ) { int t , i , j , na1 , na2 , a1 [ 10 ] , a2 [ 10 ] , cost1 = 0 , cost2 = 0 ; scanf ( "%d" , & t ) ; scanf ( "%d" , & na1 ) ; for ( i = 0 ; i < na1 ; i ++ ) { scanf ( "%d" , & a1 [ i ] ) ; } for ( i = 0 ; i < na1 - 1 ; i ++ ) { if ( a1 [ i ] > a1 [ i + 1 ] ) cost1 = cost1 + a1 [ i + 1 ] ; else cost1 = cost1 + a1 [ i ] ; } scanf ( "%d" , & na2 ) ; for ( j = 0 ; j < na2 ; j ++ ) { scanf ( "%d" , & a2 [ j ] ) ; } for ( j = 0 ; j < na2 - 1 ; j ++ ) { if ( a2 [ j ] > a2 [ j + 1 ] ) cost2 = cost2 + a2 [ j + 1 ] ; else cost2 = cost2 + a2 [ j ] ; } printf ( "%d\n\n" , cost1 ) ; printf ( "%d" , cost2 ) ; return 0 ; }