long int GCD ( long int a , long int b ) { long int divisor , D , aux ; divisor = a > b ? b : a ; D = a > b ? a : b ; while ( 1 ) { if ( ( aux = D % divisor ) == 0 ) { return divisor ; } else { D = divisor ; divisor = aux ; } } } int main ( ) { long int x , y , i , j , lcm ; int T ; scanf ( "%d" , & T ) ; scanf ( "%ld%ld" , & x , & y ) ; for ( i = 0 ; i < T ; i ++ ) { j = GCD ( x , y ) ; lcm = x * y / j ; printf ( "%ld %ld\n" , j , lcm ) ; } return 0 ; }