int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , i , sum = 0 ; scanf ( "%d" , & n ) ; int arr [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & arr [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { if ( arr [ i ] < arr [ i + 1 ] ) { sum += arr [ i ] ; arr [ i + 1 ] = arr [ i ] ; } else sum += arr [ i + 1 ] ; } printf ( "%d\n" , sum ) ; } return 0 ; }