long long int hcf ( long long int n1 , long long int n2 ) { while ( n1 != n2 ) { if ( n1 > n2 ) n1 -= n2 ; else n2 -= n1 ; } return n1 ; } long long int lcm ( long long int a , long long int b ) { return ( a * b ) / hcf ( a , b ) ; } main ( ) { long long int t ; scanf ( "%lld" , & t ) ; while ( t -- ) { long long int a , b ; scanf ( "%lld %lld" , & a , & b ) ; printf ( "%lld %lld\n" , hcf ( a , b ) , lcm ( a , b ) ) ; } }