void gcd ( long int a , long int b ) { int num , den , rem , g , l ; if ( a > b ) { num = a ; den = b ; } else { num = b ; den = a ; } while ( num % den ) { rem = num % den ; num = den ; den = rem ; } g = den ; l = ( a * b ) / g ; printf ( "%ld %ld\n" , g , l ) ; } int main ( ) { long int t , a , b ; scanf ( "%ld\n" , & t ) ; while ( t -- ) { scanf ( "%ld%ld" , & a , & b ) ; gcd ( a , b ) ; } return 0 ; }