int Calculate ( int * , int ) ; int main ( ) { int a [ 1000 ] , i , t , n , j , count ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%d" , & a [ j ] ) ; count = Calculate ( a , n ) ; printf ( "%d\n" , count ) ; } return 0 ; } int Calculate ( int * a , int n ) { int i , count = 0 , sum = 0 , temp = 0 , j , b [ 1000 ] ; b [ temp ] = - 1 ; for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { temp ++ ; b [ temp ] = i ; } } temp = temp + 1 ; b [ temp ] = i - 1 ; for ( i = 1 ; i <= temp ; i ++ ) { sum = b [ i ] - b [ i - 1 ] ; count += ( sum * ( sum + 1 ) ) / 2 ; } return count ; }