int main ( ) { long int n , q , i , p ; int t , key ; scanf ( "%d" , & t ) ; for ( ; t > 0 ; t -- ) { key = 0 ; scanf ( "%ld" , & n ) ; if ( n >= 2 ) { int a [ n ] ; q = n ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < q - 1 ; i ++ ) { if ( a [ 0 ] > a [ 1 ] ) { key += a [ 1 ] ; for ( p = 0 ; p < n - 1 ; p ++ ) a [ p ] = a [ p + 1 ] ; } else { key += a [ 0 ] ; for ( p = 1 ; p < n - 1 ; p ++ ) a [ p ] = a [ p + 1 ] ; } n -- ; } } printf ( "%d\n" , key ) ; } return 0 ; }