int main ( ) { int a , b , H , L , n ; scanf ( "%d" , & n ) ; while ( n -- ) { scanf ( "%d %d" , & a , & b ) ; H = GCD ( a , b ) ; printf ( "%d " , H ) ; L = lcm ( a , b ) ; printf ( "%d\n" , L ) ; } return 0 ; } int GCD ( int a , int b ) { if ( a == b ) return a ; if ( a % b == 0 ) return b ; if ( b % a == 0 ) return a ; if ( a > b ) return ( GCD ( a % b , b ) ) ; else return ( GCD ( a , b % a ) ) ; } int lcm ( int a , int b ) { int L ; for ( L = a > b ? a : b ; L <= a * b ; L ++ ) if ( L % a == 0 && L % b == 0 ) break ; return L ; }