int main ( ) { int t , i , a [ 100000 ] , n , sum = 0 , pos = 0 , j ; scanf ( "%d" , & t ) ; while ( t -- ) { for ( i = 0 ; i < ( n * ( n + 1 ) ) / 2 ; i ++ ) a [ i ] = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < ( n * ( n + 1 ) ) / 2 ; i ++ ) scanf ( "%d" , & a [ i ] ) ; sum = a [ 0 ] ; pos = 0 ; for ( j = 1 ; j < n ; j ++ ) { pos += j ; if ( a [ pos ] > a [ pos + 1 ] ) sum += a [ pos ] ; else sum += a [ ++ pos ] ; } printf ( "%d" , sum ) ; } return 0 ; }