int main ( ) { int t , n , i , j , k , t1 ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; } for ( j = 0 ; j < n ; j ++ ) { for ( k = j + 1 ; k < n ; k ++ ) { if ( a [ j ] < a [ k ] ) { t1 = a [ j ] ; a [ j ] = a [ k ] ; a [ k ] = t1 ; } } } printf ( "%d\n" , a [ 1 ] ) ; } return 0 ; }