int gcd ( int a , int b ) { if ( a == b ) return a ; if ( a == 0 ) return b ; if ( b == 0 ) return a ; else { if ( a % 2 == 0 && b % 2 == 0 ) return 2 * ( gcd ( a / 2 , b / 2 ) ) ; else if ( a % 2 == 0 && b % 2 != 0 ) return gcd ( a / 2 , b ) ; if ( a % 2 != 0 && b % 2 == 0 ) return gcd ( a , b / 2 ) ; if ( a % 2 != 0 && b % 2 != 0 ) if ( a > b ) return gcd ( ( a - b ) / 2 , b ) ; else return gcd ( ( b - a ) / 2 , a ) ; } } int main ( ) { int t , a , b , hcf ; long long int lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; hcf = gcd ( a , b ) ; lcm = ( a * b ) / hcf ; printf ( "%d %d\n" , hcf , lcm ) ; } }