int main ( ) { int t , n , i , c ; scanf ( "%d" , & t ) ; while ( t -- ) { int sum = 0 ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { c = a [ i + 1 ] ; } else { c = a [ i ] ; } sum += c ; } printf ( "%d\n" , sum ) ; } }