int main ( ) { int t , n ; scanf ( "%d" , & t ) ; while ( t > 0 ) { int counter = 0 ; scanf ( "%d" , & n ) ; int array [ n ] ; int j = 0 , i = 0 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & array [ j ] ) ; for ( j = i ; j < n ; j ++ ) { if ( j - i > 1 ) { if ( array [ j - 1 ] <= array [ j ] ) { counter ++ ; } else { break ; } } else { if ( array [ i ] <= array [ j ] ) { counter ++ ; } else { break ; } } } } printf ( "%d\n" , counter ) ; t -- ; } return 0 ; }