int gc ; void gcd ( int a , int b ) { int i ; for ( i = 1 ; i < a && i < b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) gc = i ; } printf ( "%d " , gc ) ; } void lcm ( int a , int b ) { printf ( "%d " , a * b / gc ) ; } int main ( ) { int t , i , f ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b ; scanf ( "%d%d" , & a , & b ) ; gcd ( a , b ) ; lcm ( a , b ) ; printf ( "\n" ) ; } return 0 ; }