int main ( void ) { int t , b , n , i ; long long int cost ; scanf ( "%d" , & t ) ; while ( t -- ) { cost = 0 ; scanf ( "%d" , & n ) ; long long int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%llu" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { cost += a [ i + 1 ] ; b = a [ i ] ; } else { cost += a [ i ] ; a [ i ] = a [ i + 1 ] ; } } printf ( "%llu\n" , cost ) ; } return 0 ; }