int main ( ) { int nTestCases ; int nArrIntegers [ 50000 ] , nIntegerCount , nMinCost ; int i , nSmallestInt = 100000 ; scanf ( "%d" , & nTestCases ) ; while ( nTestCases != 0 ) { scanf ( "%d" , & nIntegerCount ) ; PRINTD ( "%d Integers\n" , nIntegerCount ) ; for ( i = 0 ; i <= nIntegerCount - 1 ; i ++ ) { scanf ( "%d" , & nArrIntegers [ i ] ) ; PRINTD ( "%d is current Integer\n" , nArrIntegers [ i ] ) ; if ( nArrIntegers [ i ] < nSmallestInt ) { nSmallestInt = nArrIntegers [ i ] ; } } nMinCost = nSmallestInt * ( nIntegerCount - 1 ) ; printf ( "%d\n" , nMinCost ) ; nTestCases -- ; } return 0 ; }