int main ( ) { int i , j , a , b , greater , test , GCD ; long int LCM ; scanf ( "%d" , & test ) ; while ( test -- ) { printf ( "\n Enter two integers a b:" ) ; scanf ( "%d%d" , & a , & b ) ; if ( a > b ) greater = a ; else greater = b ; for ( i = 1 ; i <= greater ; i ++ ) { if ( ( a % i == 0 ) && ( b % i == 0 ) ) GCD = i ; } LCM = ( a * b ) / GCD ; printf ( "\n The GCD and LCM of %d and %d is %d  %ld" , a , b , GCD , LCM ) ; } return 0 ; }