int main ( ) { int t , n , a [ 50000 ] , i ; scanf ( "%d" , & t ) ; while ( t -- ) { int cost = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { a [ i ] = a [ i + 1 ] ; cost += a [ i ] ; } else { a [ i + 1 ] = a [ i ] ; cost += a [ i ] ; } } printf ( "%d\n" , cost ) ; } }