int getGcd ( int a , int b ) { if ( b == 0 ) return a ; else return getGcd ( b , a % b ) ; } int main ( ) { int t , gcd , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { int x , y ; scanf ( "%d %d" , & x , & y ) ; gcd = getGcd ( x , y ) ; lcm = ( x * y ) / gcd ; printf ( "%d\t%d\n" , gcd , lcm ) ; } return 0 ; }