int a [ 100000 ] , dp1 [ 100000 ] ; long long dp2 [ 100000 ] ; int main ( ) { int T , N , i ; scanf ( "%d" , & T ) ; while ( T -- ) { scanf ( "%d" , & N ) ; for ( i = 0 ; i < N ; i ++ ) scanf ( "%d" , a + i ) ; dp1 [ 0 ] = 0 ; dp2 [ 0 ] = 1 ; for ( i = 1 ; i < N ; i ++ ) if ( a [ i ] >= a [ i - 1 ] ) { dp1 [ i ] = dp1 [ i - 1 ] ; dp2 [ i ] = dp2 [ i - 1 ] + i - dp1 [ i ] + 1 ; } else { dp1 [ i ] = i ; dp2 [ i ] = dp2 [ i - 1 ] + 1 ; } printf ( "%lld" , dp2 [ N - 1 ] ) ; } return 0 ; }