int main ( ) { int x , y , n ; scanf ( "%d" , & n ) ; while ( n > 0 ) { scanf ( "%d %d" , & x , & y ) ; n -- ; int flag1 = 0 , i , store2 , store1 ; if ( x > y ) { for ( i = 1 ; i <= y ; i ++ ) { if ( x % i == 0 && y % i == 0 ) { flag1 = 1 ; store1 = i ; break ; } } } else if ( y > x ) { for ( i = 1 ; i <= x * y ; i ++ ) { if ( x % i == 0 && y % i == 0 ) { flag1 = 1 ; store1 = i ; break ; } } } store2 = ( x * y ) / store1 ; if ( flag1 == 1 ) printf ( "%d %d\n" , store1 , store2 ) ; else printf ( "%d %d\n" , x , y ) ; } }