int gcd ( int , int ) ; int lcm ( int , int ) ; int main ( ) { int n ; scanf ( "%d" , & n ) ; while ( n -- ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; printf ( "%d %d\n" , gcd ( a , b ) , lcm ( a , b ) ) ; } return 0 ; } int gcd ( int a , int b ) { if ( a == b ) return a ; else if ( a > b ) return gcd ( a - b , a ) ; else return gcd ( a , b - a ) ; } int lcm ( int a , int b ) { return ( a * b ) / gcd ( a , b ) ; }